if (shape->hasAdditionalAttribute("text:anchor-type")) {
    QString anchorType = shape->additionalAttribute("text:anchor-type");
if (shape->hasAdditionalAttribute("text:anchor-type"))
    anchorType = shape->additionalAttribute("text:anchor-type");
else if (element.hasAttributeNS(KoXmlNS::text, "anchor-type"))
    anchorType = element.attributeNS(KoXmlNS::text, "anchor-type");
else
    anchorType = "as-char"; // default value

    // page anchored shapes are handled differently
    if (anchorType != "page") {
        KoTextAnchor *anchor = new KoTextAnchor(shape);
        anchor->loadOdfFromShape(element);
        d->textSharedData->shapeInserted(shape, element, d->context);

        KoTextDocumentLayout *layout = qobject_cast<KoTextDocumentLayout*>(cursor.block().document()->documentLayout());
        if (layout) {
            KoInlineTextObjectManager *textObjectManager = layout->inlineTextObjectManager();
            if (textObjectManager) {
                textObjectManager->insertInlineObject(cursor, anchor);
            }
        }
    }
    else {
        d->textSharedData->shapeInserted(shape, element, d->context);
    }
}

function.block:
{
    const QList<Element *> elements = cells();
    const int begin = m_reverse ? elements.count() - 1 : 0;
    const int end = m_reverse ? -1 : elements.count();
    if (m_checkLock && m_sheet->cellStorage()->hasLockedCells(*this)) {
        KPassivePopup::message(i18n("Processing is not possible, because some "
                                    "cells are locked as elements of a matrix."),
                               QApplication::activeWindow());
        return false;
    }
    if (m_sheet->isProtected()) {
        for (int i = begin; i != end; m_reverse ? --i : ++i) {
            const QRect range = elements[i]->rect();

            for (int col = range.left(); col <= range.right(); ++col) {
                for (int row = range.top(); row <= range.bottom(); ++row) {
                    Cell cell(m_sheet, col, row);
                    if (m_sheet->isProtected() && !cell.style().notProtected()) {
                        KPassivePopup::message(i18n("Processing is not possible, "
                                                    "because some cells are protected."),
                                               QApplication::activeWindow());
                        return false;
                    }

                // check for matrix locks
                if (m_checkLock && cell.isLocked()) {
                    KPassivePopup::message(i18n("Processing is not possible, because some "
                                                "cells are locked as elements of a matrix."),
                                           QApplication::activeWindow());
                    return false;
                }
                }
            }
        }
    }
    return true;
}

