function.block:
{
    if (currentImage() && m_points.count() > 2) {
        QApplication::setOverrideCursor(KisCursor::waitCursor());

        KisCanvas2 * kisCanvas = dynamic_cast<KisCanvas2*>(m_canvas);
        if (!kisCanvas)
            return;

        if (!currentNode())
            return;

        KisSelectionToolHelper helper(kisCanvas, currentNode(), i18n("Polygonal Selection"));

        if (m_selectionMode == PIXEL_SELECTION) {

            KisPixelSelectionSP tmpSel = KisPixelSelectionSP(new KisPixelSelection());

            KisPainter painter(tmpSel);
            painter.setBounds(currentImage()->bounds());
            painter.setPaintColor(KoColor(Qt::black, tmpSel->colorSpace()));
            painter.setFillStyle(KisPainter::FillStyleForegroundColor);
            painter.setStrokeStyle(KisPainter::StrokeStyleNone);
            painter.setAntiAliasPolygonFill(m_optWidget->antiAliasSelection());
            painter.setOpacity(OPACITY_OPAQUE);
            painter.setPaintOpPreset(currentPaintOpPreset(), currentImage()); // And now the painter owns the op and will destroy it.
            painter.setCompositeOp(tmpSel->colorSpace()->compositeOp(COMPOSITE_OVER));
            painter.paintPolygon(m_points);

            QUndoCommand* cmd = helper.selectPixelSelection(tmpSel, m_selectAction);
            m_canvas->addCommand(cmd);
        } else {

            if (m_points.count() > 1) {
                KoPathShape* path = new KoPathShape();
                path->setShapeId(KoPathShapeId);

                QMatrix resolutionMatrix;
                resolutionMatrix.scale(1 / currentImage()->xRes(), 1 / currentImage()->yRes());
                path->moveTo(resolutionMatrix.map(m_points[0]));
                for (int i = 1; i < m_points.count(); i++)
                    path->lineTo(resolutionMatrix.map(m_points[i]));
                path->close();
                path->normalize();

                helper.addSelectionShape(path);
            }
        }
        QApplication::restoreOverrideCursor();
    }

    deactivate();
}

{
   const QPoint marker( selection->marker() );
   const QString text = Cell( this, marker ).userInput();

  Region::ConstIterator end( selection->constEnd() );
     const QRect range = (*it)->rect();

    int bottom = range.bottom();
    if (bottom > d->cellStorage->rows()) bottom = d->cellStorage->rows();
     for ( int col = range.left(); col <= range.right(); ++col ) {
       for ( int row = range.top(); row <= range.bottom()bottom; ++row )
       {
      int col = range.left() - 1;
      while (1) {
        const Cell cell( this, col, row ) = d->cellStorage->nextInRow (col, row);
        if (cell.isNull()) break;
        col = cell.column();
        if (cell.isDefault() || (col == 0) || (col > range.right())) break;

        if ( !cell.isPartOfMerged() && !( col == marker.x() && row == marker.y() ) )
         {
           if ( !cell.isFormula() && !cell.value().isNumber() &&
                !cell.value().asString().isEmpty() &&
                !cell.isTime() && !cell.isDate() )
           {
             if ( cell.userInput() != text )
               return true;
           }
         }
      }
       }
     }
   return false;
}

